import React, { useState } from 'react';
import RuleForm from './components/RuleForm';
import CombineRules from './components/CombineRules';
import EvaluateRules from './components/EvaluateRules'; // Fixed import path

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [evaluationResult, setEvaluationResult] = useState(null); // Used for storing evaluation result
  const [error, setError] = useState(null);
  const [inputData, setInputData] = useState({
    age: '',
    department: '',
    salary: '',
    experience: '',
  });

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputData,
      [name]: value,
    });
  };

  return (
    <div className="App">
      <h1>Rule Engine</h1>
      <RuleForm onAddRule={addRule} />
      <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
      {combinedAST && (
        <>
          <EvaluateRules
            combinedAST={combinedAST}
            inputData={inputData} // Pass the inputData to EvaluateRules
            setEvaluationResult={setEvaluationResult}
            setError={setError}
          />
          <div>
            <h2>Evaluation Result:</h2>
            <p>{evaluationResult !== null ? (evaluationResult ? 'True' : 'False') : 'No result yet'}</p>
          </div>
        </>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <h2>Input Data</h2>
        <form>
          <label>
            Age:
            <input
              type="number"
              name="age"
              value={inputData.age}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Department:
            <input
              type="text"
              name="department"
              value={inputData.department}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Salary:
            <input
              type="number"
              name="salary"
              value={inputData.salary}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Experience:
            <input
              type="number"
              name="experience"
              value={inputData.experience}
              onChange={handleInputChange}
            />
          </label>
        </form>
      </div>
    </div>
  );
}

export default App;
