// src/App.js
import React, { useState } from 'react';
import RuleForm from './components/RuleForm';
import CombineRules from './components/CombineRules';
import EvaluateRules from './components/CombineRules';

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [error, setError] = useState(null);

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  return (
    <div className="App">
      <h1>Rule Engine</h1>
      <RuleForm onAddRule={addRule} />
      <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
      {combinedAST && <EvaluateRules combinedAST={combinedAST} />}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
