import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box, Grid, Paper } from '@mui/material';
import RuleForm from './components/RuleForm';
import CombineRules from './components/CombineRules';
import EvaluateRules from './components/EvaluateRules';

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [evaluationResult, setEvaluationResult] = useState(null);
  const [error, setError] = useState(null);
  const [inputData, setInputData] = useState({
    age: '',
    department: '',
    salary: '',
    experience: '',
  });
  const [astRule, setAstRule] = useState('');

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputData,
      [name]: value,
    });
  };

  const handleAstRuleChange = (e) => {
    setAstRule(e.target.value);
  };

  return (
    <Container>
      <Typography variant="h3" align="center" gutterBottom>
        Rule Engine
      </Typography>
      <Box my={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <RuleForm onAddRule={addRule} />
        </Paper>
      </Box>
      <Box my={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
        </Paper>
      </Box>
      <Box my={4}>
        <Paper elevation={3} style={{ padding: '20px' }}>
          <div>
            <Typography variant="h5" gutterBottom>Evaluate Rule</Typography>
            <TextField
              fullWidth
              label="AST Rule"
              value={astRule}
              onChange={handleAstRuleChange}
              placeholder="Enter AST rule"
              margin="normal"
            />
            <form>
              <Grid container spacing={2}>
                <Grid item xs={6} md={3}>
                  <TextField
                    fullWidth
                    label="Age"
                    type="number"
                    name="age"
                    value={inputData.age}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item xs={6} md={3}>
                  <TextField
                    fullWidth
                    label="Department"
                    name="department"
                    value={inputData.department}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item xs={6} md={3}>
                  <TextField
                    fullWidth
                    label="Salary"
                    type="number"
                    name="salary"
                    value={inputData.salary}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item xs={6} md={3}>
                  <TextField
                    fullWidth
                    label="Experience"
                    type="number"
                    name="experience"
                    value={inputData.experience}
                    onChange={handleInputChange}
                  />
                </Grid>
              </Grid>
            </form>
          </div>
          <Box my={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                if (combinedAST) {
                  // Call EvaluateRules function here with astRule and inputData
                }
              }}
            >
              Evaluate
            </Button>
          </Box>
          {evaluationResult !== null && (
            <Box my={2}>
              <Typography variant="h5">Evaluation Result:</Typography>
              <Typography variant="body1">{evaluationResult ? 'True' : 'False'}</Typography>
            </Box>
          )}
          {combinedAST && (
            <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
              <Typography variant="h6">Combined AST:</Typography>
              <pre>{JSON.stringify(combinedAST, null, 2)}</pre>
            </Paper>
          )}
        </Paper>
      </Box>
      {error && <Typography color="error">{error}</Typography>}
    </Container>
  );
}

export default App;
