import React, { useState } from 'react';

function RuleForm({ onAddRule }) {
  const [ruleString, setRuleString] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    // Perform validation if needed
    if (ruleString.trim() === '') {
      setError('Rule cannot be empty');
      return;
    }

    try {
      // Send the rule to the backend
      const response = await fetch('http://localhost:5000/api/rules', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ruleString: ruleString }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
    console.log("result");
      // Optionally, call the onAddRule prop to update the UI
      onAddRule(result.rule); // Assuming the backend returns the created rule
      
      // Clear the input
      setRuleString('');
      setError(null); // Clear any previous errors
    } catch (error) {
      setError('Failed to add rule');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Rule</h2>
      <label>
        Rule:
        <input
          type="text"
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
          placeholder="e.g., age > 30 AND department = 'Sales'"
        />
      </label>
      <button type="submit">Add Rule</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
}

export default RuleForm;
