import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';

function CombineRules({ rules, setCombinedAST, setError }) {
  const [rulesArray, setRulesArray] = useState('');

  const handleCombine = async () => {
    try {
      const response = await fetch('/api/combineRules', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rules: rulesArray.split(',') }),
      });

      const data = await response.json();

      if (response.ok) {
        setCombinedAST(data.combinedAST);
      } else {
        throw new Error(data.message || 'Failed to combine rules');
      }
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      <Typography variant="h5" gutterBottom>Combine Rules</Typography>
      <Box display="flex" alignItems="center">
        <TextField
          label="Rules Array"
          value={rulesArray}
          onChange={(e) => setRulesArray(e.target.value)}
          placeholder="e.g., rule1,rule2,rule3"
          fullWidth
        />
        <Button onClick={handleCombine} variant="contained" color="secondary" style={{ marginLeft: '10px' }}>
          Combine
        </Button>
      </Box>
    </div>
  );
}

export default CombineRules;
