// src/CombineRules.js
import React from 'react';
import axios from 'axios';

const CombineRules = ({ rules, setCombinedAST, setError }) => {
  const handleCombine = async () => {
    try {
      const response = await axios.post('http://localhost:5000/combine-rules', { rules });
      setCombinedAST(response.data);
    } catch (err) {
      setError(err.response ? err.response.data.error : 'An error occurred');
    }
  };

  return (
    <div>
      <h2>Combine Rules</h2>
      <button onClick={handleCombine} disabled={rules.length === 0}>
        Combine Rules
      </button>
    </div>
  );
};

export default CombineRules;
