// src/components/RuleCombiner.js
import React, { useState } from 'react';
import axios from 'axios';

const RuleCombiner = () => {
  const [rules, setRules] = useState(['']);
  const [combinedAST, setCombinedAST] = useState(null);
  const [error, setError] = useState(null);

  const handleRuleChange = (index, event) => {
    const newRules = [...rules];
    newRules[index] = event.target.value;
    setRules(newRules);
  };

  const addRule = () => {
    setRules([...rules, '']);
  };

  const combineRules = async () => {
    try {
      const response = await axios.post('http://localhost:3000/combine-rules', { rules });
      setCombinedAST(response.data);
      setError(null);
    } catch (error) {
      setError(error.response ? error.response.data.error : error.message);
    }
  };

  return (
    <div>
      <h1>Rule Combiner</h1>
      {rules.map((rule, index) => (
        <div key={index}>
          <input
            type="text"
            value={rule}
            onChange={(e) => handleRuleChange(index, e)}
            placeholder={`Rule ${index + 1}`}
          />
        </div>
      ))}
      <button onClick={addRule}>Add Rule</button>
      <button onClick={combineRules}>Combine Rules</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {combinedAST && (
        <pre>{JSON.stringify(combinedAST, null, 2)}</pre>
      )}
    </div>
  );
};

export default RuleCombiner;
