}

function EvaluateRules({ combinedAST, inputData, setEvaluationResult, setError }) {
  const [shouldEvaluate, setShouldEvaluate] = useState(false);

  const handleEvaluate = () => {
    try {
      if (shouldEvaluate) return; // Prevent re-evaluation
      const result = evaluateRule(combinedAST, inputData);
      setEvaluationResult(result);
      setError(null);
      setShouldEvaluate(true); // Mark as evaluated
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      <button onClick={handleEvaluate}>Evaluate</button>
      {evaluationResult !== null && (
        <p>
          The rule has been evaluated: {evaluationResult ? 'True' : 'False'}
        </p>
      )}
    </div>
  );
}

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [evaluationResult, setEvaluationResult] = useState(null);
  const [error, setError] = useState(null);
  const [inputData, setInputData] = useState({
    age: '',
    department: '',
    salary: '',
    experience: '',
  });

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputData,
      [name]: value,
    });
  };

  return (
    <div className="App">
      <h1>Rule Engine</h1>
      <RuleForm onAddRule={addRule} />
      <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
      {combinedAST && (
        <>
          <EvaluateRules
            combinedAST={combinedAST}
            inputData={inputData}
            setEvaluationResult={setEvaluationResult}
            setError={setError}
          />
          <div>
            <h2>Evaluation Result:</h2>
          </div>
        </>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <h2>Input Data</h2>
        {/* ... input fields for data */}
      </div>
    </div>
  );
}

export default App;
