import React, { useState } from 'react';
import RuleForm from './components/RuleForm';
import CombineRules from './components/CombineRules';
import EvaluateRules from './components/EvaluateRules'; // Fixed import path

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [evaluationResult, setEvaluationResult] = useState(null); // Used for storing evaluation result
  const [error, setError] = useState(null);

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  return (
    <div className="App">
      <h1>Rule Engine</h1>
      <RuleForm onAddRule={addRule} />
      <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
      {combinedAST && <EvaluateRules combinedAST={combinedAST} setEvaluationResult={setEvaluationResult} setError={setError} />}
      {1 && (
        <div>
          <h2>Evaluation Result:</h2>
          <p>{evaluationResult ? 'True' : 'False'}</p>
        </div>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
