import React, { useState } from 'react';
import RuleForm from './components/RuleForm';
import CombineRules from './components/CombineRules';
import EvaluateRules from './components/EvaluateRules';

function App() {
  const [rules, setRules] = useState([]);
  const [combinedAST, setCombinedAST] = useState(null);
  const [evaluationResult, setEvaluationResult] = useState(null);
  const [error, setError] = useState(null);

  const addRule = (rule) => {
    setRules([...rules, rule]);
  };

  const evaluateCombinedRules = async () => {
    try {
      const response = await fetch('http://localhost:5000/evaluate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ast: combinedAST }),
      });
      const result = await response.json();
      setEvaluationResult(result);
    } catch (err) {
      setError('Failed to evaluate rules.');
    }
  };

  return (
    <div className="App">
      <h1>Rule Engine</h1>
      <RuleForm onAddRule={addRule} />
      <CombineRules rules={rules} setCombinedAST={setCombinedAST} setError={setError} />
      {combinedAST && (
        <div>
          <button onClick={evaluateCombinedRules}>Evaluate Combined Rules</button>
          <EvaluateRules combinedAST={combinedAST} evaluationResult={evaluationResult} />
        </div>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
