import React, { useState } from 'react';

function EvaluateRules({ combinedAST, setEvaluationResult, setError }) {
  const [inputData, setInputData] = useState({
    age: '',
    department: '',
    salary: '',
    experience: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInputData({ ...inputData, [name]: value });
  };

  const handleEvaluate = async () => {
    try {
      const response = await fetch('http://localhost:5000/evaluate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ast: combinedAST, data: inputData }),
      });
      const result = await response.json();
      setEvaluationResult(result);
    } catch (err) {
      setError('Failed to evaluate rules.');
    }
  };

  return (
    <div>
      <h2>Evaluate Rules</h2>
      <label>
        Age:
        <input
          type="number"
          name="age"
          value={inputData.age}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Department:
        <input
          type="text"
          name="department"
          value={inputData.department}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Salary:
        <input
          type="number"
          name="salary"
          value={inputData.salary}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Experience:
        <input
          type="number"
          name="experience"
          value={inputData.experience}
          onChange={handleInputChange}
        />
      </label>
      <button onClick={handleEvaluate} disabled={!combinedAST}>
        Evaluate
      </button>
      {evaluationResult !== null && (
        <div>
          <h3>Evaluation Result:</h3>
          <p>{evaluationResult ? 'True' : 'False'}</p>
        </div>
      )}
    </div>
  );
}

export default EvaluateRules;
